import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';

@Component({
  selector: 'app-root',
  imports: [RouterOutlet],
  templateUrl: './app.html',
  styleUrl: './app.scss',
})
export class App {
  protected title = 'TodoList.Client';
}

import { Component } from '@angular/core';
import { ErrorStateMatcher } from '@angular/material/core';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { ReactiveFormsModule, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-email-input',
  imports: [
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
  ],
  templateUrl: './email-input.html',
  styleUrl: './email-input.scss'
})
export class EmailInput {
  emailControl: FormControl = new FormControl('', [Validators.required, Validators.email]);

  constructor(public errorStateMatcher: ErrorStateMatcher) {}

  isEmailEmpty(): boolean {
    return this.emailControl.hasError('required');
  }

  isEmailInvalid(): boolean {
    return this.emailControl.hasError('email') && !this.emailControl.hasError('required');
  }
}

import { Component, Input, signal } from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { ReactiveFormsModule } from '@angular/forms';
import { EmailInput } from '../common/email-input/email-input';
import { PasswordInput } from '../common/password-input/password-input';
